package trainingSelenium;

public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n, count;
		
		System.out.println("The even numbers are:");
		for(n=100; n<=200; n++){
			
			if(n%2==0){
				count = n;
				System.out.println(count);
				
			}
							
		}
		
	}

}
